import styled from 'styled-components';

export const Section = styled.section`
  align-items: center;  
  background-color: gray;
  display: flex;
  flex-wrap: wrap;
`

export const Div = styled.div`
  align-items: center;
  background-color: white;
  display: flex;
  flex-direction: column;
  heigth: 350px;
  margin: 15px;
  padding: 5px;
  width: 300px;
`

export const Img = styled.img`
  heigth: 150px;
  width: 150px;
`
