import styled from 'styled-components';

export const LI = styled.li`
  list-style: none;
  &:hover {
    background-color: green;
  }
`

export const Div = styled.div`
  display: ${props => props.visibility ? 'none' : 'in-block'};
  position: relative;
  left: 0;
  width: 25%;
`
