import styled from 'styled-components';

export const LI = styled.li`
  color: white;
  list-style: none;
  margin-top: 5px;
  margin-bottom: 5px;
  width: 220px;

  &:hover {
    color: black;
    background-color: rgb(243, 168, 71);
  }
`

export const Div = styled.div`
  display: ${props => props.visibility ? 'none' : 'in-block'};
  margin-top: 60px;
  background-color: black; 
  opacity: 0.9;
  position: absolute;
  left: 0;
  width: 50%;

  display: flex;
  flex-wrap: wrap;
  heigth: 80vh;
`
