export const localeCurrency = (value) => {
  return value.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })
}

export const inputsGenerator = (type, name, placeholder, labelText) => {
  return (
    <label htmlFor={ `${name}-input` }>
      { labelText }
      <input
        type={ type }
        name={ name }
        id={ `${name}-input` }
        placeholder={ placeholder }
        required
      />
    </label>
  )
  }

export const paymentMethodInputs = (type, labelText) => {
  return (
    <label htmlFor={ type }>
    { labelText }
    <input
      id={ type }
      type="radio"
      name="payment"
      value={ type }
    />
    </label>
  );
}
