import styled from 'styled-components';

export const LI = styled.li`
  list-style: none;

  &:hover {
    background-color: green;
  }
`

export const Div = styled.div`
  display: ${props => props.visibility ? 'none' : 'in-block'};
`

export const Section = styled.section`
  width: 20%;
  position: relative;
  left: 0;
`
