import styled from 'styled-components';

export const Div = styled.div`
  display: flex;
  flex-direction: column;
  background-color: black;
  align-items: center;
`

export const Button = styled.button`
  background-color: blue;
  border-radius: 15px;
  width: 150px;
`

export const Span = styled.span`
  width: 15px;
`
