import React from 'react';
import PropTypes from 'prop-types';

class OrderSummary extends React.Component {
  constructor(props) {
    super(props);
    const { location } = this.props;
    const { state } = location;
    this.state = {
      order: [...state],
      totalPrice: 0,
    };
    this.productsReview = this.productsReview.bind(this);
    this.totalPrice = this.totalPrice.bind(this);
    this.paymentMethod = this.paymentMethod.bind(this);
    this.inputsGenerator = this.inputsGenerator.bind(this);
  }

  productsReview() {
    const { order, totalPrice } = this.state;
    return (
      <div>
        <h1>Revise seus Produtos</h1>
        {
          order.map(({ title, thumbnail, quantity, id, price }) => (
            <div key={ id }>
              <img src={ thumbnail } alt={ `foto ${title}` } />
              <span>{ title }</span>
              <span>
                R$
                { price * quantity }
              </span>
            </div>
          ))
        }
        <p>
          Preço Total: R$
          { totalPrice }
        </p>
      </div>
    );
  }

  totalPrice() {
    const { order } = this.state;
    const total = order
      .reduce((acc, product) => acc + (product.price * product.quantity), 0);
    this.setState({
      totalPrice: total,
    });
  }

  inputsGenerator(type, name, placeholder, labelText) {
    return (
      <label htmlFor={ `${name}-input` }>
        { labelText }
        <input
          type={ type }
          name={ name }
          id={ `${name}-input` }
          placeholder={ placeholder }
          required
        />
      </label>
    )
  }

  paymentMethod() {
    return (
      <div>
        <label htmlFor="boleto">
          Boleto
          <input
            id="boleto"
            type="radio"
            name="payment"
          />
        </label>
        <label htmlFor="creditcard">
          Cartão de crédito
          <input
            id="creditcard"
            type="radio"
            name="payment"
            value="visa"
          />
          Visa
          <input
            id="creditcard"
            type="radio"
            name="payment"
            value="mastercard"
          />
          MasterCard
          <input
            id="creditcard"
            type="radio"
            name="payment"
            value="elo"
          />
          Elo
        </label>
      </div>
    );
  }

  renderForm() {
    return (
      <form>
        <div>
          { this.inputsGenerator('text', 'name', 'Nome completo', 'Digite seu nome completo') }
          { this.inputsGenerator('text', 'CPF', 'CPF', 'Digite seu CPF') }
          { this.inputsGenerator('text', 'email', 'E-mail', 'Digite seu E-mail') }
          { this.inputsGenerator('text', 'telefone', 'Telefone', 'Digite seu Telefone') }
          { this.inputsGenerator('text', 'CEP', 'CEP', 'Digite seu CEP') }
          { this.inputsGenerator('text', 'address', 'Endereço', 'Digite seu Endereço') }
        </div>
        { this.paymentMethod() }
        <button
          type="button"
          onClick={ () => console.log('XABLAU!') }
        >
          Finalizar a Compra!
        </button>
      </form>
    );
  }

  render() {
    console.log(this.props);
    return (
      <div>
        { this.productsReview() }

        { this.renderForm() }
      </div>
    );
  }
}

OrderSummary.propTypes = {
  location: PropTypes.shape({
    state: PropTypes.arrayOf(),
  }).isRequired,
};

export default OrderSummary;
