import styled from 'styled-components';

export const LI = styled.li`
  color: white;
  list-style: none;
  margin-bottom: 5px;

  &:hover {
    background-color: rgb(243, 168, 71);
  }
`

export const Div = styled.div`
  display: ${props => props.visibility ? 'none' : 'in-block'};
  margin-top: 60px;
  background-color: black; 
  opacity: 0.8;
  position: absolute;
  left: 0;
  width: 35%;
`
