import React from 'react';

export const localeCurrency = (value) => {
  return value.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })
}

export const inputsGenerator = (type, name, placeholder, labelText) => {
  return (
    <label htmlFor={ `${name}-input` }>
      { labelText }
      <input
        type={ type }
        name={ name }
        id={ `${name}-input` }
        placeholder={ placeholder }
        required
      />
    </label>
  )
  }

export const paymentMethodInputs = (type) => {
  const method = type.toLowerCase();
  method[0].toUpperCase();
  return (
    <label htmlFor={ method }>
    { method }
    <input
      id={ method }
      type="radio"
      name="payment"
      value={ method }
    />
    </label>
  );
}

const wordFormater = (word) => {
  const lower = word.toLowerCase()
  const formated = lower[0].toUpperCase() + lower.substr(1);
  return formated
}
